
// custom imports glsl files
declare module '*.vert' 
{
    const content: string;
    export default content;
}
declare module '*.frag' 
{
    const content: string;
    export default content;
}
declare module '*.glsl' 
{
    const content: string;
    export default content;
}

// imports sounds
declare module '*.mp3'
{
    const value: any;
    export default value;
}
// imports images
declare module '*.jpg'
{
    const value: any;
    export default value;
}
declare module '*.png'
{
    const value: any;
    export default value;
}
declare module '*.svg'
{
    const value: any;
    export default value;
}


